import React from 'react'
import { useEffect, useState } from 'react'
import useAuth from '../Dashboard/useAuth'
import SpotifyWebApi from 'spotify-web-api-node'
import axios from 'axios'

/* set up spotifyApi */
const spotifyApi = new SpotifyWebApi({
  clientId: 'a1f4ee3c6e5d4760ae3953c8c1328491'
})

export const Home = ({ code }) => {
  const [name, setName] = useState('')
  // const accessToken = useAuth(code)

  useEffect(() => {
    axios.post("http://localhost:3001/spotify/login", { code })
    .then(res => {
      /* cuts code from URL if successful */
      window.history.pushState({}, null, "/")

      setAccessToken(res.data.accessToken)
      setRefreshToken(res.data.refreshToken)
      setExpiresIn(res.data.expiresIn)
    })
    .catch(err => {
      /* returns to home page on fail */
      console.log(err)
      // window.location = "/"
    })
  }, [code])

  useEffect(() => {
    if (!accessToken) return

    /* initialize spotifyApi with access token */
    spotifyApi.setAccessToken(accessToken)

    spotifyApi.getMe().then(data => {
      console.log(data)
      setName(data.body.display_name)
    })
  }, [])
  return (
    <div>{name}</div>
  )
}
