import React, { useState, useEffect } from 'react'
import { accessToken, logout, getCurrentUserProfile, getCurrentUserPlaylist } from './spotify'
import { getInPlaylistProfile, addInPlaylistProfile } from './playlist'
import axios from 'axios'
import styled from 'styled-components/macro'
import Dropdown from 'react-dropdown'
import 'react-dropdown/style.css'

const StyledLoginButton = styled.a`
  background-color: green;
  color: white;
  padding: 10px 20px;
  margin: 20px auto;
  border-radius: 30px;
  display: inline-block;
`

function Dashboard({
  clearToken
}) {
  const [spotifyToken, setSpotifyToken] = useState(null)
  const [profile, setProfile] = useState(null)
  const [playlist, setPlaylist] = useState(null)
  const [inPlaylistProfile, setInPlaylistProfile] = useState(null)
  const [currentAddPlaylist, setCurrentAddPlaylist] = useState(null)

  /* get value of tokens out of the URL */
  useEffect(() => {
    setSpotifyToken(accessToken)
    const fetchUserProfile = async () => {
      try {
        const { data } = await getCurrentUserProfile()
        setProfile(data)
      } catch (error) {
        console.log(error)
      }
    }

    const fetchUserPlaylist = async () => {
      try {
        const { data } = await getCurrentUserPlaylist()
        setPlaylist(data.items)
      } catch (error) {
        console.log(error)
      }
    }

    const fetchInPlaylistProfile = async () => {
      try {
        const { data } = await getInPlaylistProfile()
        setInPlaylistProfile(data)
      } catch (error) {
        console.log(error)
      }
    }
    fetchUserProfile()
    fetchUserPlaylist()
    fetchInPlaylistProfile()
  }, [])

  useEffect(() => {
    console.log(inPlaylistProfile)
    console.log(playlist)

    const newPlaylistArray = []
    const newInPlaylistArray = []
    if (inPlaylistProfile !== null) {
      for (let i = 0; i < inPlaylistProfile.length; i++) {
        newInPlaylistArray.push(inPlaylistProfile[i])
      }
    }
    if (playlist !== null) {
      for (let i = 0; i < playlist.length; i++) {
        console.log(playlist[i])
        if (!inPlaylistProfile?.includes(playlist[i])) {
          newPlaylistArray.push(playlist[i])
        }
      }
    }
    // const newArray = playlist?.filter((item, idx) => !inPlaylistProfile.includes(playlist[idx]))
    console.log(newPlaylistArray)
    // setPlaylist(newArray)
  }, [playlist, inPlaylistProfile])

  const handleAddPlaylistOnClick = async () => {
    /* work on how to update an item in db */
    try {
      const { data } = await addInPlaylistProfile(currentAddPlaylist)
      console.log(data)
    } catch (error) {
      console.log(error)
    }
  }

  const clearAllTokens = () => {
    clearToken()
    logout()
  }

  return (
    <div className="dashboard">
      {spotifyToken ? (
      <>
        <h1>Logged In</h1>
        {profile && (
          <div>
            <h1>{profile.display_name}</h1>
            <p>{profile.followers.total} Followers</p>
            {profile.images.length > 0 ? <img src={profile.images[0].url} alt="Profile Picture"/> : null}
          </div>
        )}
        {playlist && (
          <div>
            <Dropdown 
              options={playlist.map(item => ({value: item, label: item.name}))} 
              onChange={(e) => setCurrentAddPlaylist(e.value)}
              placeholder="Select a playlist to add to your profile"
            />
            <button className="add-playlist" disabled={currentAddPlaylist === null} onClick={handleAddPlaylistOnClick}>Add Playlist to Profile</button>
          </div>
        )}
      </>)
       : <StyledLoginButton className="App-link" href="http://localhost:8888/spotify/login">Log Into Spotify</StyledLoginButton>}
      <button className="logout" onClick={clearAllTokens}>Log Out</button>
      {spotifyToken ? <button onClick={logout}>Log Out of Spotify</button> : null}
    </div>
  )
}

export default Dashboard
