import React from 'react'
import { loginUrl } from './spotify'
import { Home } from '../Home/Home'

function Dashboard({
  clearToken
}) {
  /* get value of tokens out of the URL */
  const code = new URLSearchParams(window.location.search).get('code')

  return (
    <div className="dashboard">
      {/* {code ? <Home code={code}/> : 
      <div id="login">
        <h1>First, log in to spotify</h1>
        <a href={loginUrl}>Sign Into Spotify</a>
      </div>} */}
      <a className="App-link" href="http://localhost:8888/spotify/login">Log Into Spotify</a>
      <button className="logout" onClick={clearToken}>Log Out</button>
    </div>
  )
}

export default Dashboard
