import React, { useState, useEffect } from 'react'
import { accessToken, logout, getCurrentUserProfile, getCurrentUserPlaylist } from './spotify'
import styled from 'styled-components/macro'

const StyledLoginButton = styled.button`
  background-color: green;
  color: white;
  padding: 10px 20px;
  margin: 20px auto;
  border-radius: 30px;
  display: inline-block;
`

function Dashboard({
  clearToken
}) {
  const [spotifyToken, setSpotifyToken] = useState(null)
  const [profile, setProfile] = useState(null)
  const [playlist, setPlaylist] = useState(null)
  /* get value of tokens out of the URL */
  useEffect(() => {
    setSpotifyToken(accessToken)
    const fetchUserProfile = async () => {
      try {
        const { data } = await getCurrentUserProfile()
        setProfile(data)
      } catch (error) {
        console.log(error)
      }
    }

    const fetchUserPlaylist = async () => {
      try {
        const { data } = await getCurrentUserPlaylist()
        console.log(data.items)
        setPlaylist(data.items)
      } catch (error) {
        console.log(error)
      }
    }
    fetchUserProfile()
    fetchUserPlaylist()

  }, [])

  return (
    <div className="dashboard">
      {spotifyToken ? (
      <>
        <h1>Logged In</h1>
        {profile && (
          <div>
            <h1>{profile.display_name}</h1>
            <p>{profile.followers.total} Followers</p>
            {profile.images.length > 0 ? <img src={profile.images[0].url} alt="Profile Picture"/> : null}
